mapper.select:
SELECT MEMBER_NUMBER
FROM TBL_MEMBER
WHERE MEMBER_ID = #{memberId} and MEMBER_PASSWORD = #{memberPassword}

select member_id
from tbl_member
where member_name = #{memberName} and member_phone_number = #{memberPhoneNumber}

select member_Password
from tbl_member
where member_name = #{memberName} and member_phone_number = #{memberPhoneNumber} and member_id = #{memberId}

SELECT MEMBER_ID
FROM TBL_MEMBER
WHERE MEMBER_NUMBER = #{memberNumber}

SELECT count(member_number)
FROM TBL_MEMBER
WHERE MEMBER_NUMBER = #{memberNumber} and MEMBER_PASSWORD = #{memberPassword}

SELECT count(member_number)
FROM TBL_MEMBER
WHERE  MEMBER_NICKNAME = #{memberNickname}

mapper.update:
UPDATE TBL_MEMBER 
SET MEMBER_PASSWORD = #{memberPassword}, MEMBER_NICKNAME = #{memberNickname}, 
	MEMBER_ADDRESS_NUMBER = #{memberAddressNumber}, 
	MEMBER_EMAIL = #{memberEmail}, MEMBER_PHONE_NUMBER = #{memberPhoneNumber}
WHERE MEMBER_NUMBER = #{memberNumber}
